import { StyleSheet, Text, TextInput, TextField, View, Image, SafeAreaView, TouchableOpacity } from "react-native";
import React from "react";

const Login = ({ navigation, route }) => {
    const [text, onChangeText] = React.useState('Useless Text');
    const [number, onChangeNumber] = React.useState('');

	return (
		<View style={{ flexDirection: "column", flex: 1, justifyContent: 'center', marginHorizontal: 30, alignItems: "center"}}>
        <SafeAreaView>
            <TextInput
                style={styles.inputProp}
                onChangeText={onChangeText}
                value={text}
            />
            <TextInput
                style={styles.inputProps}
                onChangeText={onChangeNumber}
                value={number}
                placeholder="useless placeholder"
                keyboardType="numeric"
            />
            </SafeAreaView>			
			<TouchableOpacity
					style={{
						backgroundColor: "#f96163",
						borderRadius: 8,
						paddingVertical: 15,
						width: "100%",
						alignItems: "center",
						marginBottom: 20
					}}>
					<Text style={{ fontSize: 20, color: "#fff", fontWeight: "700" }}>
					Sign In
					</Text>
			</TouchableOpacity>
        </View>
	);
};

export default Login;

const styles = StyleSheet.create({
    inputprops: {
        backgroundColor: "rgba(255, 165, 0, 0.48)",
        height: 50,
        width: 350,
        borderRadius: 5,
        margin: 10,
        marginBottom: 10,
        padding: 10,
    },
});
