import { StyleSheet, Text, View, FlatList, Image, Dimensions } from "react-native";
import React, { useEffect, useRef, useState } from "react";
import {Slider1, Slider2, Slider3, Slider4} from "../CarouselImages";

const Carousel = () => {
    const flatListRef = useRef();
    const screenWidth = Dimensions.get("window").width;
    const [activeIndex, setActiveIndex] = useState(0);
    const [currentIndex, setCurrentIndex] = useState(0);
    // console.log("data",activeIndex);
    // useEffect(() => {
    //     let interval = setInterval(()=>{
    //         if(activeIndex === carouselData.length - 1) {
    //             flatListRef.current.scrollToIndex({
    //                 index: 0,
    //                 animation: true,
    //             });
    //         } else {
    //             flatListRef.current.scrollToIndex({
    //                 index: setActiveIndex( activeIndex + 1),
    //                 animation: true,
    //             });
    //         }
    //     }, 3000);
    //     return () => clearInterval(interval);
    // },[]);
  useEffect(() => {
    myRef.current.scrollToIndex({
      animated: true,
      index: currentIndex ,
    });
  }, [currentIndex]);
    useEffect(()=> {
        const timer = setTimeout(()=> {
        const nextIndex = currentIndex === carouselData.length - 1
            ? 0
            : currentIndex + 1;
        setCurrentIndex(nextIndex);
        }, 1000);
        return () => clearTimeout(timer);
    });


    const getItemLayout = (data, index) =>({
        length: screenWidth,
        offset: screenWidth * index,
        index: index,
    });

    const carouselData = [
        {
            id: "01",
            imageUrl: Slider1,
        },
        {
            id: "02",
            imageUrl: Slider2,
        },
        {
            id: "03",
            imageUrl: Slider3,
        },  
        {
            id: "04",
            imageUrl: Slider4,
        },
    ];

    const renderItem = ({ item, data }) => {
        return(
            <View>
                <Image source={item.imageUrl} style={{ height:200, width: screenWidth }}></Image>
            </View>
        );
    };

    const handleScroll = (event) =>{
        const scrollPosition = event.nativeEvent.contentOffset.x;
        const index = scrollPosition / screenWidth;
        setActiveIndex(index);
    }
    return (
        <View>
            <FlatList data={carouselData} 
                renderItem={renderItem}
                ref={flatListRef}
                getItemLayout={getItemLayout}
                keyExtractor={(item)=>item.id} 
                snapToAlignment="center"
                decelerationRate={"fast"}
                showsHorizontalScrollIndicator={false}
                horizontal={true}
                scrollEnabled={true} 
                pagingEnabled={true}
                useScrollView={true}
                onScroll={handleScroll}>
            </FlatList>
        </View>
    )
}

export default Carousel;

const styles = StyleSheet.create({});
