import { StyleSheet, Text, View, Image, Button, TouchableOpacity } from "react-native";
import React from "react";

const Login = ({ navigation, route }) => {
	return (
		<View style={{ flex: 1, justifyContent: 'center', marginHorizontal: 30, alignItems: "center"}}>
			<Text style={{ flex: 1, fontSize: 22, fontWeight: "700" }}>
				hi
			</Text>	
            <Button title="Sign In" style={{ color: "#f96163", fontSize: 22, fontWeight: "bold" }} />		
			    <TouchableOpacity
							style={{
								backgroundColor: "#f96163",
								borderRadius: 8,
								paddingVertical: 15,
								width: "100%",
								alignItems: "center",
								marginBottom: 20
							}}
						>
							<Button style={{ fontSize: 18, color: "#fff", fontWeight: "700" }}>
								Order Now
							</Button>
						</TouchableOpacity>
        </View>
	);
};

export default Login;

const styles = StyleSheet.create({});
