import { StyleSheet, Text, TextInput, TextField, View, Image, SafeAreaView, TouchableOpacity } from "react-native";
import React from "react";

const Login = ({ navigation, route }) => {
    const [onChangeText] = React.useState('');

	return (
		<View style={{ backgroundColor: "rgb(233 233 233)", flexDirection: "column", flex: 1, justifyContent: 'center', alignItems: "center"}}>
        <SafeAreaView style={{marginHorizontal: 30}}>
            <TextInput
                style={styles.inputProps}
                onChangeText={onChangeText}
                placeholder="Username"
            />
            <TextInput
                style={styles.inputProps}
                onChangeText={onChangeText}
                placeholder="Password"
            />
            <Text>Forgot Password?</Text>
            </SafeAreaView>			
			<TouchableOpacity
					style={{
						backgroundColor: "#f96163",
						borderRadius: 8,
						paddingVertical: 15,
						width: "85%",
						alignItems: "center",
						marginTop: 20,
                        padding: 10,
					}}>
					<Text style={{ fontSize: 20, color: "#fff", fontWeight: "700" }}>
					Sign In
					</Text>
			</TouchableOpacity>
        </View>
	);
};

export default Login;

const styles = StyleSheet.create({
    inputProps: {
        backgroundColor: "rgb(255 255 255)",
        height: 50,
        width: 350,
        borderRadius: 5,
        margin: 10,
        marginBottom: 10,
        padding: 10,
    },
    shadowProp: {
		shadowColor: '#000',
		shadowOffset: {width: 2, height: 4},
		shadowOpacity: 0.4,
		shadowRadius: 6,
    },
});
